mapper.select:
select * from booking
where member_email =#{email.email} and
bo_status like '예약취소%';

mapper.insert:
insert into booking (bo_checkin, bo_checkout, bo_status,
bo_paymethod, bo_price, member_email, room_ro_num)
values (#{bo_checkin}, #{bo_checkout}, '예약전', #{bo_paymethod}, #{bo_price},
#{email}, #{room_ro_num});

insert into payment (booking_bo_num, pay_method, pay_amount, pay_status)
values (#{bo_num}, #{bo_paymethod}, #{bo_price}, '결제전');
